pragma solidity ^0.8.19;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {IPauser} from "./Pauser.sol";

/**
 * @title Pausable
 * @notice This contract, in conjunction with Pauser, allows an inheriting contract to be paused and
 *  unpaused.
 * @author Goldfinch
 */

abstract contract Pausable is Initializable {
  IPauser pauser;

  function __Pausable_init(IPauser _pauser) internal onlyInitializing {
    require(address(_pauser) != address(0), "ZA");
    pauser = _pauser;
  }

  modifier whenNotPaused() {
    _requireNotPaused();
    _;
  }

  function _requireNotPaused() internal view virtual {
    require(!paused(), "Pausable: paused");
  }

  function paused() public view virtual returns (bool) {
    return pauser.isPaused(address(this));
  }

  uint256[100] private __gap;
}
","keccak256":"0x1ecfb7ee1e330b005303d2ee238cc69645e16b20f4c719d736dabc6b93bd0f16","license":"MIT"},"contracts/protocol/core/Pauser.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {AccessControlUpgradeable} from "./access/AccessControlUpgradeable.sol";
import {IPauser} from "../../interfaces/IPauser.sol";

/**
 * @title Pauser
 * @notice This contract is used to pause/unpause other contracts in the protocol, in conjunction with
 *  Pausable. It also provides a single global pause/unpause switch that pauses all contracts at once.
 * @author Goldfinch
 */

abstract contract Pauser is AccessControlUpgradeable, IPauser {
  bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

  bool internal globalPaused;
  mapping(address => bool) internal pausedContracts;

  // solhint-disable-next-line func-name-mixedcase
  function __Pauser_init() public onlyInitializing {}

  function isPaused(address addr) external view returns (bool) {
    return globalPaused || pausedContracts[addr];
  }

  function pause(address addr) external onlyPauserRole {
    pausedContracts[addr] = true;
    emit Paused(addr);
  }

  function unpause(address addr) external onlyPauserRole {
    pausedContracts[addr] = false;
    emit Unpaused(addr);
  }

  function globalPause() external onlyPauserRole {
    globalPaused = true;
    emit GlobalPaused();
  }

  function globalUnpause() external onlyPauserRole {
    globalPaused = false;
    emit GlobalUnpaused();
  }

  modifier onlyPauserRole() {
    /// @dev NA: not authorized
    require(hasRole(PAUSER_ROLE, _msgSender()), "NA");
    _;
  }

  uint256[50] private __gap1;
  uint256[50] private __gap2;
}","keccak256":"0x45a0658ccfc936cd12572d3896479b10e5f53497cbae0e194e31453e3384a59d","license":"MIT"},"contracts/protocol/core/access/AccessControlUpgradeable.sol":{"content":"// SPDX-License-Identifier: MIT

